-- Test two identical logn strings.
CREATE OR REPLACE PROCEDURE TEST_1_LONG ()
 BEGIN
  DECLARE ACTUAL_MSG ANCHOR DB2UNIT_2_BETA.REPORT_TESTS.MESSAGE;
  DECLARE EXPECTED_MSG ANCHOR DB2UNIT_2_BETA.REPORT_TESTS.MESSAGE;
  DECLARE STRING1 VARCHAR(32672);
  DECLARE STRING2 VARCHAR(32672);

  SET EXPECTED_MSG = 'Message check';
  INSERT INTO DB2UNIT_ASSERTIONS_STRING_LONG.REPORT_TESTS (DATE, EXECUTION_ID,
    TEST_NAME, MESSAGE) VALUES (CURRENT TIMESTAMP, 0, '', EXPECTED_MSG);
  SET STRING1 = GET_LONG_STRING();
  SET STRING2 = STRING1;

  BEGIN
   DECLARE TOO_LONG CONDITION FOR SQLSTATE '22001';
   DECLARE CONTINUE HANDLER FOR TOO_LONG
     CALL DB2UNIT.FAIL();
   CALL DB2UNIT.ASSERT_STRING_EQUALS(STRING1, STRING2);
  END;

  SELECT MESSAGE INTO ACTUAL_MSG
    FROM DB2UNIT_ASSERTIONS_STRING_LONG.REPORT_TESTS
    WHERE DATE = (SELECT MAX(DATE) FROM DB2UNIT_ASSERTIONS_STRING_LONG.REPORT_TESTS);

  DELETE FROM DB2UNIT_ASSERTIONS_STRING_LONG.REPORT_TESTS
    WHERE EXECUTION_ID = 0
    AND MESSAGE = EXPECTED_MSG
    AND DATE = (SELECT MAX(DATE) FROM DB2UNIT_ASSERTIONS_STRING_LONG.REPORT_TESTS);

  CALL DB2UNIT.ASSERT_STRING_EQUALS(EXPECTED_MSG, ACTUAL_MSG);
 END @

